<template>
    <v-app id="inspire">
        <v-content>
            <v-container fluid fill-height>
                <v-layout align-center justify-center>
                    <v-flex xs12 sm8 md3>
                        <v-card class="elevation-12">
                            <v-toolbar dark color="primary">
                                <v-toolbar-title>Register</v-toolbar-title>
                            </v-toolbar>
                            <v-card-text>
                                <v-form>
                                    <v-text-field label="Username" type="text" v-model="user.username" v-validate="'required'" name="username" :class="{ 'is-invalid': submitted && errors.has('username') }"></v-text-field>
                                    <v-text-field label="User e-mail" type="text" v-model="user.email" v-validate="'required'" name="email" :class="{ 'is-invalid': submitted && errors.has('email') }"></v-text-field>
                                    <v-text-field label="Password" type="password" v-model="user.password" v-validate="'required'" name="password" :class="{ 'is-invalid': submitted && errors.has('password') }"></v-text-field>
                                    <v-text-field label="Password confirmation" type="passwordConfirmation" v-model="user.passwordConfirmation" v-validate="'required'" name="passwordConfirmation" :class="{ 'is-invalid': submitted && errors.has('passwordConfirmation') }"></v-text-field>
                                </v-form>
                            </v-card-text>
                            <v-card-actions>
                                <v-spacer></v-spacer>
                                <v-btn color="primary" @click="handleSubmit" :disabled="status.registering">Register</v-btn>
                                <img v-show="status.registering" src="data:image/gif;base64," />
                                <router-link to="/login" class="btn btn-link"><v-btn color="primary">Cancel</v-btn></router-link>
                            </v-card-actions>
                        </v-card>
                    </v-flex>
                </v-layout>
            </v-container>
        </v-content>
    </v-app>
</template>

<script>
import { mapState, mapActions } from 'vuex'

export default {
    data () {
        return {
            user: {
                username: '',
                email: '',
                password: '',
                passwordConfirmation: ''
            },
            submitted: false
        }
    },
    computed: {
        ...mapState('account', ['status'])
    },
    methods: {
        ...mapActions('account', ['register']),
        handleSubmit(e) {
            this.submitted = true;
            this.$validator.validate().then(valid => {
                if (valid) {
                    this.register(this.user);
                }
            });
        }
    }
};
</script>